import{_ as t,c as r,o as n,a2 as i}from"./chunks/framework.D3KFGq5-.js";const g=JSON.parse('{"title":"Operatoren für Multicasting","description":"Erklärung der Multicasting-bezogenen Operatoren in RxJS. Verwendungsunterschiede zwischen share, shareReplay, publish und multicast, Konvertierung von Cold zu Hot, effiziente Werteverteilung an mehrere Subscriber, Maßnahmen gegen Memory Leaks und mehr - praktische Multicast-Strategien werden vorgestellt. Mit TypeScript-Typinferenz wird typensichere Stream-Freigabe realisiert und Implementierungsmuster zur Performance-Optimierung vermittelt.","frontmatter":{"description":"Erklärung der Multicasting-bezogenen Operatoren in RxJS. Verwendungsunterschiede zwischen share, shareReplay, publish und multicast, Konvertierung von Cold zu Hot, effiziente Werteverteilung an mehrere Subscriber, Maßnahmen gegen Memory Leaks und mehr - praktische Multicast-Strategien werden vorgestellt. Mit TypeScript-Typinferenz wird typensichere Stream-Freigabe realisiert und Implementierungsmuster zur Performance-Optimierung vermittelt."},"headers":[],"relativePath":"de/guide/operators/multicasting/index.md","filePath":"de/guide/operators/multicasting/index.md","lastUpdated":1765295683000}'),a={name:"de/guide/operators/multicasting/index.md"};function s(d,e,o,u,l,c){return n(),r("div",null,[...e[0]||(e[0]=[i('<h1 id="operatoren-fur-multicasting" tabindex="-1">Operatoren für Multicasting <a class="header-anchor" href="#operatoren-fur-multicasting" aria-label="Permalink to &quot;Operatoren für Multicasting&quot;">​</a></h1><p>In RxJS stehen mehrere spezielle Operatoren zur Verfügung, um &quot;Multicasting&quot; zu realisieren - das Teilen der Ausgabe eines Observables mit mehreren Subscribern.</p><p>Diese Seite stellt aus der <strong>Perspektive der Operatoren</strong> die wichtigsten Multicasting-bezogenen Operatoren vor und organisiert deren Verwendungsunterschiede und Vorsichtsmaßnahmen.</p><blockquote><p>❗ Für konzeptionelle Erklärungen von Multicast, strukturelle Beschreibungen unter Verwendung von Subject und konkrete Codebeispiele siehe bitte <a href="/RxJS-with-TypeScript/de/guide/subjects/multicasting">Multicasting-Mechanismus</a>.</p></blockquote><h2 id="hauptsachliche-multicasting-bezogene-operatoren" tabindex="-1">Hauptsächliche Multicasting-bezogene Operatoren <a class="header-anchor" href="#hauptsachliche-multicasting-bezogene-operatoren" aria-label="Permalink to &quot;Hauptsächliche Multicasting-bezogene Operatoren&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Operator</th><th>Merkmale</th><th>Bemerkungen</th></tr></thead><tbody><tr><td><strong><a href="/RxJS-with-TypeScript/de/guide/operators/multicasting/share">share()</a></strong></td><td>Einfachster Multicast-Mechanismus. Intern äquivalent zu <code>publish().refCount()</code></td><td>Für die meisten Anwendungsfälle ausreichend</td></tr><tr><td><strong><a href="/RxJS-with-TypeScript/de/guide/operators/multicasting/shareReplay">shareReplay()</a></strong></td><td>Zusätzlich zu Multicast werden die letzten Werte bei erneutem Abonnement bereitgestellt</td><td>Wenn Zustandswiederverwendung erforderlich ist</td></tr><tr><td><code>publish()</code> + <code>refCount()</code></td><td>Multicast-Konfiguration mit kontrollierbarem Ausführungstiming</td><td>Klassische und flexible Konfiguration</td></tr><tr><td><code>multicast()</code></td><td>Low-Level-API, die explizit ein <code>Subject</code> übergibt</td><td>Nützlich bei Verwendung von benutzerdefinierten Subjects</td></tr></tbody></table><h2 id="vergleich-der-multicasting-muster" tabindex="-1">Vergleich der Multicasting-Muster <a class="header-anchor" href="#vergleich-der-multicasting-muster" aria-label="Permalink to &quot;Vergleich der Multicasting-Muster&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Operator</th><th>Merkmale</th><th>Anwendungsfall</th></tr></thead><tbody><tr><td><strong><a href="/RxJS-with-TypeScript/de/guide/operators/multicasting/share">share()</a></strong></td><td>Grundlegendes Multicasting</td><td>Gleichzeitige Nutzung in mehreren Komponenten</td></tr><tr><td><strong><a href="/RxJS-with-TypeScript/de/guide/operators/multicasting/shareReplay">shareReplay(n)</a></strong></td><td>Puffert die letzten n Werte</td><td>Verspätete Subscription/Zustandsfreigabe</td></tr><tr><td><code>publish() + refCount()</code></td><td>Feinere Kontrolle möglich</td><td>Wenn erweiterte Kontrolle erforderlich ist</td></tr><tr><td><code>multicast(() =&gt; new Subject())</code></td><td>Vollständige Anpassung</td><td>Wenn spezielle Subject-Typen benötigt werden</td></tr></tbody></table><h2 id="vorsichtsmaßnahmen-bei-verwendung-von-multicasting" tabindex="-1">Vorsichtsmaßnahmen bei Verwendung von Multicasting <a class="header-anchor" href="#vorsichtsmaßnahmen-bei-verwendung-von-multicasting" aria-label="Permalink to &quot;Vorsichtsmaßnahmen bei Verwendung von Multicasting&quot;">​</a></h2><ol><li><strong>Timing verstehen</strong>: Verstehen, dass sich die empfangenen Werte je nach Zeitpunkt des Subscription-Starts unterscheiden</li><li><strong>Lifecycle-Management</strong>: Besonders bei Verwendung von <code>refCount</code> wird der Stream beendet, wenn die Anzahl der Subscriber null wird</li><li><strong>Fehlerbehandlung</strong>: Wenn ein Fehler im gemulticasteten Observable auftritt, betrifft dies alle Subscriber</li><li><strong>Speicherverwaltung</strong>: Bei Verwendung von <code>shareReplay</code> etc. auf Memory Leaks achten</li></ol>',10)])])}const p=t(a,[["render",s]]);export{g as __pageData,p as default};
