import{_ as e,c as a,o as r,a2 as o}from"./chunks/framework.D3KFGq5-.js";const u=JSON.parse('{"title":"Utility Operators","description":"Utility operators are a group of auxiliary operators in RxJS that are responsible for controlling side effects, delay processing, subscription management, etc.","frontmatter":{"description":"Utility operators are a group of auxiliary operators in RxJS that are responsible for controlling side effects, delay processing, subscription management, etc."},"headers":[],"relativePath":"en/guide/operators/utility/index.md","filePath":"en/guide/operators/utility/index.md","lastUpdated":1763074329000}'),i={name:"en/guide/operators/utility/index.md"};function d(n,t,s,c,l,h){return r(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="utility-operators" tabindex="-1">Utility Operators <a class="header-anchor" href="#utility-operators" aria-label="Permalink to &quot;Utility Operators&quot;">​</a></h1><p>Utility operators in RxJS are a group of operators that are responsible for <strong>auxiliary processing of streams (side effects, state control, UI support, etc.)</strong> rather than the main purpose of data conversion or filtering.</p><p>On this page, operators are categorized by purpose as shown below, and a list is provided to confirm their basic usage. For detailed usage and practical examples, please refer to the respective pages or <a href="./practical-use-cases.html">Practical Use Cases</a>.</p><h2 id="list-of-operators-by-purpose" tabindex="-1">List of Operators (by Purpose) <a class="header-anchor" href="#list-of-operators-by-purpose" aria-label="Permalink to &quot;List of Operators (by Purpose)&quot;">​</a></h2><h3 id="◾-side-effects-and-state-control" tabindex="-1">◾ Side Effects and State Control <a class="header-anchor" href="#◾-side-effects-and-state-control" aria-label="Permalink to &quot;◾ Side Effects and State Control&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Operator</th><th>Description</th><th>Often Combined With</th></tr></thead><tbody><tr><td><a href="./tap.html">tap</a></td><td>Execute side effects without changing values (log output, UI updates, etc.)</td><td><code>map</code>, <code>switchMap</code></td></tr><tr><td><a href="./finalize.html">finalize</a></td><td>Execute cleanup processing when the stream ends</td><td><code>tap</code>, <code>catchError</code></td></tr></tbody></table><h3 id="◾-timing-and-delay-control" tabindex="-1">◾ Timing and Delay Control <a class="header-anchor" href="#◾-timing-and-delay-control" aria-label="Permalink to &quot;◾ Timing and Delay Control&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Operator</th><th>Description</th><th>Often Combined With</th></tr></thead><tbody><tr><td><a href="./delay.html">delay</a></td><td>Delay the emission of each value by a specified time</td><td><code>tap</code>, <code>concatMap</code></td></tr><tr><td><a href="./timeout.html">timeout</a></td><td>Generate an error if emission exceeds a certain time</td><td><code>catchError</code>, <code>retry</code></td></tr><tr><td><a href="./takeUntil.html">takeUntil</a></td><td>End subscription when the specified Observable notifies</td><td><code>interval</code>, <code>fromEvent</code></td></tr></tbody></table><h3 id="◾-initial-value-repetition-array-conversion-etc" tabindex="-1">◾ Initial Value, Repetition, Array Conversion, etc. <a class="header-anchor" href="#◾-initial-value-repetition-array-conversion-etc" aria-label="Permalink to &quot;◾ Initial Value, Repetition, Array Conversion, etc.&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Operator</th><th>Description</th><th>Often Combined With</th></tr></thead><tbody><tr><td><a href="./startWith.html">startWith</a></td><td>Emit an initial value at the beginning of the stream</td><td><code>scan</code>, <code>combineLatest</code></td></tr><tr><td><a href="./repeat.html">repeat</a></td><td>Resubscribe to the entire stream after completion</td><td><code>tap</code>, <code>delay</code></td></tr><tr><td><a href="./retry.html">retry</a></td><td>Retry on error</td><td><code>catchError</code>, <code>switchMap</code></td></tr><tr><td><a href="./toArray.html">toArray</a></td><td>Emit all values in the stream as a single array (on completion)</td><td><code>concatMap</code>, <code>take</code></td></tr></tbody></table><h2 id="remarks" tabindex="-1">Remarks <a class="header-anchor" href="#remarks" aria-label="Permalink to &quot;Remarks&quot;">​</a></h2><ul><li>Difference between <code>retry</code> and <code>repeat</code>: <ul><li><code>retry</code>: <strong>Retry on error</strong></li><li><code>repeat</code>: <strong>Retry on successful completion</strong></li></ul></li><li><code>toArray</code> does not output a value unless it completes, so it is commonly used with <code>take()</code> and so on.</li></ul>',12)])])}const f=e(i,[["render",d]]);export{u as __pageData,f as default};
