import{_ as t,c as a,o as r,a2 as i}from"./chunks/framework.D3KFGq5-.js";const f=JSON.parse(`{"title":"Opérateurs utilitaires","description":"Les opérateurs utilitaires aident à contrôler les effets de bord, la gestion des délais et la gestion des abonnements dans RxJS. L'utilisation et les modèles pratiques de tap, delay, finalize, takeUntil, startWith, retry, repeat et d'autres opérateurs fréquemment utilisés en pratique sont expliqués.","frontmatter":{"description":"Les opérateurs utilitaires aident à contrôler les effets de bord, la gestion des délais et la gestion des abonnements dans RxJS. L'utilisation et les modèles pratiques de tap, delay, finalize, takeUntil, startWith, retry, repeat et d'autres opérateurs fréquemment utilisés en pratique sont expliqués."},"headers":[],"relativePath":"fr/guide/operators/utility/index.md","filePath":"fr/guide/operators/utility/index.md","lastUpdated":1765219888000}`),d={name:"fr/guide/operators/utility/index.md"};function o(s,e,n,l,u,c){return r(),a("div",null,[...e[0]||(e[0]=[i('<h1 id="operateurs-utilitaires" tabindex="-1">Opérateurs utilitaires <a class="header-anchor" href="#operateurs-utilitaires" aria-label="Permalink to &quot;Opérateurs utilitaires&quot;">​</a></h1><p>Les opérateurs utilitaires de RxJS sont un <strong>groupe d&#39;opérateurs qui ne sont pas destinés à la conversion ou au filtrage des données, mais au traitement auxiliaire du flux (effets de bord, contrôle de l&#39;état, support de l&#39;interface utilisateur, etc.)</strong>.</p><p>Sur cette page, les opérateurs sont classés par objectif comme suit, et une liste est fournie pour identifier leurs utilisations de base. Pour une utilisation détaillée et des exemples pratiques, voir les pages individuelles ou les <a href="./practical-use-cases">Cas d&#39;utilisation pratiques</a>.</p><h2 id="liste-des-operateurs-par-fonction" tabindex="-1">Liste des opérateurs (par fonction) <a class="header-anchor" href="#liste-des-operateurs-par-fonction" aria-label="Permalink to &quot;Liste des opérateurs (par fonction)&quot;">​</a></h2><h3 id="◾-effets-de-bord-et-controle-de-l-etat" tabindex="-1">◾ Effets de bord et contrôle de l&#39;état <a class="header-anchor" href="#◾-effets-de-bord-et-controle-de-l-etat" aria-label="Permalink to &quot;◾ Effets de bord et contrôle de l&#39;état&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Opérateur</th><th>Description</th><th>Opérateurs fréquemment combinés</th></tr></thead><tbody><tr><td><a href="./tap">tap</a></td><td>Effectuer des effets de bord sans changer les valeurs (sortie de journal, mises à jour de l&#39;interface utilisateur, etc.)</td><td><code>map</code>, <code>switchMap</code></td></tr><tr><td><a href="./finalize">finalize</a></td><td>Effectuer un nettoyage à la fin du flux</td><td><code>tap</code>, <code>catchError</code></td></tr></tbody></table><h3 id="◾-controle-du-timing-et-des-delais" tabindex="-1">◾ Contrôle du timing et des délais <a class="header-anchor" href="#◾-controle-du-timing-et-des-delais" aria-label="Permalink to &quot;◾ Contrôle du timing et des délais&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Opérateur</th><th>Description</th><th>Opérateurs fréquemment combinés</th></tr></thead><tbody><tr><td><a href="./delay">delay</a></td><td>Retarder l&#39;émission de chaque valeur pour un temps spécifié</td><td><code>tap</code>, <code>concatMap</code></td></tr><tr><td><a href="./timeout">timeout</a></td><td>Une erreur se produit si l&#39;émission dépasse un certain temps</td><td><code>catchError</code>, <code>retry</code></td></tr><tr><td><a href="./takeUntil">takeUntil</a></td><td>L&#39;abonnement se termine lorsque l&#39;Observable spécifié notifie</td><td><code>interval</code>, <code>fromEvent</code></td></tr></tbody></table><h3 id="◾-valeur-initiale-repetition-conversion-en-tableau-etc" tabindex="-1">◾ Valeur initiale, répétition, conversion en tableau, etc. <a class="header-anchor" href="#◾-valeur-initiale-repetition-conversion-en-tableau-etc" aria-label="Permalink to &quot;◾ Valeur initiale, répétition, conversion en tableau, etc.&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Opérateur</th><th>Description</th><th>Opérateurs fréquemment combinés</th></tr></thead><tbody><tr><td><a href="./startWith">startWith</a></td><td>Valeur initiale émise au début du flux</td><td><code>scan</code>, <code>combineLatest</code></td></tr><tr><td><a href="./repeat">repeat</a></td><td>Réabonner le flux entier après achèvement</td><td><code>tap</code>, <code>delay</code></td></tr><tr><td><a href="./retry">retry</a></td><td>Réessai en cas d&#39;erreur</td><td><code>catchError</code>, <code>switchMap</code></td></tr><tr><td><a href="./toArray">toArray</a></td><td>Publie toutes les valeurs du flux dans un tableau (à la fin)</td><td><code>concatMap</code>, <code>take</code></td></tr></tbody></table><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>Différence entre <code>retry</code> et <code>repeat</code> : <ul><li><code>retry</code> : <strong>réessai en cas d&#39;erreur</strong></li><li><code>repeat</code> : <strong>réessai en cas de succès</strong></li></ul></li><li><code>toArray</code> ne produit pas de valeur tant qu&#39;il n&#39;est pas terminé, donc il est couramment utilisé en conjonction avec <code>take()</code>, etc.</li></ul>',12)])])}const h=t(d,[["render",o]]);export{f as __pageData,h as default};
